import React,{useState} from 'react';
import { Counter } from './features/counter/Counter';
import Header from './components/header/Header';
import RightSidebar from './components/sidebar/RightSidebar';
import MainLayout from './layout/MainLayout';
import NewMessagePopup from './components/newMessagePopup/NewMessagePopup';
import './App.css';

function App() {
  const [isNewMessage, setIsNewMessage] = useState(false);

  const newMessageHandler = (isBool) => {
    setIsNewMessage(isBool);
  }
  return (
    <div>
        <Header/>
        <div className="content">
          <RightSidebar onHandlerClick={this.newMessageHandler}/>
          <MainLayout/>
        </div>
        {isNewMessage && <NewMessagePopup/>}
        <Counter/>
    </div>
 );
}

export default App;
